import mongoose from "mongoose";

const userSchema = new mongoose.Schema(
  {
    name: {
      type: String,
      required: true,
    },
    email: {
      type: String,
      unique: true,
    },
    password: {
      type: String,
      required: true,
    },
    img: {
      type: String,
      default:
        "data:image/jpeg;base64,",
    },
    gender: {
      type: String,
      default: "not sepecified",
    },
    age: {
      type: String,
      default: "00",
    },
    phoneNo: {
      type: String,
      default: "0000000000",
    },
  },
  { timestamps: true }
);

const User = mongoose.models.User || mongoose.model("User", userSchema);

export default User;
